(2, 1, '2017-03-23 19:35:35', '2017-03-23 19:35:35', '[av_section min_height=\'custom\' min_height_px=\'960px\' padding=\'default\' shadow=\'no-shadow\' bottom_border=\'no-border-styling\' bottom_border_diagonal_color=\'#333333\' bottom_border_diagonal_direction=\'\' bottom_border_style=\'\' id=\'\' color=\'main_color\' custom_bg=\'#000000\' src=\'\' attachment=\'\' attachment_size=\'\' attach=\'scroll\' position=\'top left\' repeat=\'no-repeat\' video=\'http://bioee.dev/wp-content/uploads/2017/videos/Splash Screen.mp4\' video_ratio=\'16:9\' overlay_opacity=\'0.5\' overlay_color=\'\' overlay_pattern=\'\' overlay_custom_pattern=\'\' av_element_hidden_in_editor=\'0\']\r\n[av_textblock size=\'40\' font_color=\'custom\' color=\'#ffffff\' admin_preview_bg=\'\']\r\n<p style="text-align: center;">The Bioelectronic Systems Laboratory:\r\nMixed analog-digital CMOS integrated circuit design</p>\r\n[/av_textblock]\r\n[/av_section]\r\n\r\n[av_section min_height=\'\' min_height_px=\'500px\' padding=\'default\' shadow=\'no-shadow\' bottom_border=\'no-border-styling\' bottom_border_diagonal_color=\'#333333\' bottom_border_diagonal_direction=\'\' bottom_border_style=\'\' id=\'People\' color=\'main_color\' custom_bg=\'\' src=\'\' attachment=\'\' attachment_size=\'\' attach=\'scroll\' position=\'top left\' repeat=\'no-repeat\' video=\'\' video_ratio=\'16:9\' overlay_opacity=\'0.5\' overlay_color=\'\' overlay_pattern=\'\' overlay_custom_pattern=\'\' av_element_hidden_in_editor=\'0\']\r\n\r\n[av_heading tag=\'h3\' padding=\'10\' heading=\'Meet Our Team\' color=\'\' style=\'\' custom_font=\'\' size=\'\' subheading_active=\'\' subheading_size=\'15\' custom_class=\'\' admin_preview_bg=\'\'][/av_heading]\r\n\r\n[av_textblock size=\'18\' font_color=\'\' color=\'\' admin_preview_bg=\'\']\r\n<span style="color: #000000; font-family: arial, helvetica, sans-serif;">The Bioelectronic Systems Laboratory is comprised of a</span><span style="color: #333333; font-family: roboto, arial, sans-serif;">