(1307, 1, '2017-04-11 05:08:54', '2017-04-11 05:08:54', '<?php\r\n	global $avia_config;\r\n\r\n	/*\r\n	 * get_header is a basic wordpress function, used to retrieve the header.php file in your theme directory.\r\n	 */\r\n	 get_header();\r\n\r\n	$title  = __(\'Blog - Latest News\', \'avia_framework\'); //default blog title\r\n	$t_link = home_url(\'/\');\r\n	$t_sub = "";\r\n\r\n	if(avia_get_option(\'frontpage\') && $new = avia_get_option(\'blogpage\'))\r\n	{\r\n		$title 	= get_the_title($new); //if the blog is attached to a page use this title\r\n		$t_link = get_permalink($new);\r\n		$t_sub =  avia_post_meta($new, \'subtitle\');\r\n	}\r\n\r\n	if( get_post_meta(get_the_ID(), \'header\', true) != \'no\') echo avia_title(array(\'heading\'=>\'strong\', \'title\' => $title, \'link\' => $t_link, \'subtitle\' => $t_sub));\r\n	\r\n	//do_action( \'ava_after_main_title\' );\r\n\r\n?>\r\n\r\n		<div class=\'container_wrap container_wrap_first main_color <?php avia_layout_class( \'main\' ); ?>\'>\r\n\r\n			<div class=\'container template-blog template-single-blog \'>\r\n\r\n				<main class=\'content units <?php avia_layout_class( \'content\' ); ?> <?php echo avia_blog_class_string(); ?>\' <?php avia_markup_helper(array(\'context\' => \'content\',\'post_type\'=>\'post\'));?>>\r\n\r\n				<div id="people_profile_wrapper">\r\n	<div id="people_profile_photo">\r\n		\r\n		\r\n        [if photo]\r\n        	<img src="{@photo}">\r\n        [else]\r\n        <img src="/wp-content/uploads/2017/04/bio_nopic-150x150.png">\r\n		[/if]\r\n		\r\n    </div>\r\n    <div id ="people_profile_info">\r\n        <h3>{@name}</h3>\r\n        <h4>\r\n		[if lab_title]\r\n        {@lab_title}\r\n        [/if]\r\n		</h4><BR/>\r\n		[if email_address]\r\n		<strong>Email:</strong> <a href="mailto:{@email_address}">{@email_address}</a><br/>\r\n		[/if]\r\n        [if telephone]\r\n        <strong>Telephone:</strong> {@telephone}<br/>\r\n        [/if]\r\n		[if address]\r\n		<strong>Office:</strong> {@address}<br/>\r\n        [/if]\r\n		[if cv]\r\n        {@cv}<br/>\r\n        [/if]\r\n		[if education]\r\n        {@education}\r\n        [/if]\r\n\r\n	</div>\r\n    <div style="clear: both;"></div>\r\n	<div id="people_profile_bio">\r\n        \r\n        [if research]\r\n        {@research}\r\n        [/if]\r\n		[if awards]\r\n        {@awards}\r\n        [/if]\r\n	</div>\r\n</div>\r\n\r\n				<!--end content-->\r\n				</main>\r\n\r\n				<?php\r\n				$avia_config[\'currently_viewing\'] = "blog";\r\n				//get the sidebar\r\n				get_sidebar();\r\n\r\n\r\n				?>\r\n\r\n\r\n			</div><!--end container-->\r\n\r\n		</div><!-- close default .container_wrap element -->\r\n\r\n\r\n<?php get_footer(); ?>', 'people-profile', '', 'inherit', 'closed', 'closed', '', '225-revision-v1', '', '', '2017-04-11 05:08:54', '2017-04-11 05:08:54', '', 225, 'http://bioee.dev/225-revision-v1/', 0, 'revision', '', 0),
